BEGIN;

CREATE TABLE users(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	email CHARACTER VARYING(150) NOT NULL,
	password CHARACTER VARYING(25) NOT NULL,
	CONSTRAINT pk_users PRIMARY KEY (id),
	CONSTRAINT uk_users_email UNIQUE (email)
);

CREATE TABLE company_staffs(
	user_id INTEGER NOT NULL,
	first_name CHARACTER VARYING(50) NOT NULL,
	last_name CHARACTER VARYING(50) NOT NULL,
	CONSTRAINT pk_company_staffs PRIMARY KEY (user_id),
	CONSTRAINT fk_company_staffs_users FOREIGN KEY (user_id) REFERENCES users (id)
);

CREATE TABLE candidates(
	user_id INTEGER NOT NULL,
	first_name CHARACTER VARYING(50) NOT NULL,
	last_name CHARACTER VARYING(50) NOT NULL,
	identity_number CHARACTER(11) NOT NULL,
	date_of_birth DATE NOT NULL,
	CONSTRAINT pk_candidates PRIMARY KEY (user_id),
	CONSTRAINT fk_candidates_users FOREIGN KEY (user_id) REFERENCES users (id),
	CONSTRAINT uk_candidates_identity_number UNIQUE (identity_number)
);

CREATE TABLE employers(
	user_id INTEGER NOT NULL,
	company_name CHARACTER VARYING(150) NOT NULL,
	web_address CHARACTER VARYING(150) NOT NULL,
	phone_number CHARACTER(15) NOT NULL,
	CONSTRAINT pk_employers PRIMARY KEY (user_id),
	CONSTRAINT fk_employers_users FOREIGN KEY (user_id) REFERENCES users (id)
);

CREATE TABLE updated_employers(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	employer_id INTEGER NOT NULL,
	email CHARACTER VARYING(150) NOT NULL,
	password CHARACTER VARYING(25) NOT NULL,
	company_name CHARACTER VARYING(150) NOT NULL,
	web_address CHARACTER VARYING(150) NOT NULL,
	phone_number CHARACTER(15) NOT NULL,
	CONSTRAINT pk_updated_employers PRIMARY KEY (id),
	CONSTRAINT fk_updated_employers_employers FOREIGN KEY (employer_id) REFERENCES employers (user_id)
);

CREATE TABLE user_activations(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	user_id INTEGER NOT NULL,
	code CHARACTER VARYING(50) NOT NULL,
	is_activated BOOLEAN NOT NULL,
	is_activated_date TIMESTAMP WITH TIME ZONE NOT NULL,
	CONSTRAINT pk_user_activations PRIMARY KEY (id),
	CONSTRAINT fk_user_activations_users FOREIGN KEY (user_id) REFERENCES users (id),
	CONSTRAINT uk_user_activations_user_id UNIQUE (user_id),
	CONSTRAINT uk_user_activations_code UNIQUE (code)
);

CREATE TABLE user_confirmation_types(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	type CHARACTER VARYING(50) NOT NULL,
	CONSTRAINT pk_user_confirmation_types PRIMARY KEY (id)
);

CREATE TABLE user_confirmations(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	user_id INTEGER NOT NULL,
	company_staff_id INTEGER NOT NULL,
	user_confirmation_type_id INTEGER NOT NULL,
	is_confirmed BOOLEAN NOT NULL,
	is_confirmed_date TIMESTAMP WITH TIME ZONE NOT NULL,
	CONSTRAINT pk_user_confirmations PRIMARY KEY (id),
	CONSTRAINT fk_user_confirmations_users FOREIGN KEY (user_id) REFERENCES users (id),
	CONSTRAINT fk_user_confirmations_company_staffs FOREIGN KEY (company_staff_id) REFERENCES company_staffs (user_id),
	CONSTRAINT fk_user_confirmations_user_confirmation_types FOREIGN KEY (user_confirmation_type_id) REFERENCES user_confirmation_types (id)
);

CREATE TABLE job_titles(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	title CHARACTER VARYING(150) NOT NULL,
	CONSTRAINT pk_job_titles PRIMARY KEY (id),
	CONSTRAINT uk_job_titles_title UNIQUE (title)
);

CREATE TABLE cities(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	city CHARACTER VARYING(50) NOT NULL,
	CONSTRAINT pk_cities PRIMARY KEY (id),
	CONSTRAINT uk_cities_city UNIQUE (city)
);

CREATE TABLE working_times(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	time CHARACTER VARYING(50) NOT NULL,
	CONSTRAINT pk_working_times PRIMARY KEY (id),
	CONSTRAINT uk_working_times_time UNIQUE (time)
);

CREATE TABLE working_types(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	type CHARACTER VARYING(50) NOT NULL,
	CONSTRAINT pk_working_types PRIMARY KEY (id),
	CONSTRAINT uk_working_types_type UNIQUE (type)
);

CREATE TABLE job_postings(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	employer_id INTEGER NOT NULL,
	job_title_id INTEGER NOT NULL,
	city_id INTEGER NOT NULL,
	working_time_id INTEGER NOT NULL,
	working_type_id INTEGER NOT NULL,
	job_description CHARACTER VARYING(2300) NOT NULL,
	number_of_open_positions INTEGER NOT NULL,
	salary_min CHARACTER VARYING(50),
	salary_max CHARACTER VARYING(50),
	posting_date TIMESTAMP WITH TIME ZONE NOT NULL,
	closing_date TIMESTAMP WITH TIME ZONE NOT NULL,
	is_active BOOLEAN NOT NULL,
	CONSTRAINT pk_job_postings PRIMARY KEY (id),
	CONSTRAINT fk_job_postings_employers FOREIGN KEY (employer_id) REFERENCES employers (user_id),
	CONSTRAINT fk_job_postings_job_titles FOREIGN KEY (job_title_id) REFERENCES job_titles (id),
	CONSTRAINT fk_job_postings_cities FOREIGN KEY (city_id) REFERENCES cities (id),
	CONSTRAINT fk_job_postings_working_times FOREIGN KEY (working_time_id) REFERENCES working_times (id),
	CONSTRAINT fk_job_postings_working_types FOREIGN KEY (working_type_id) REFERENCES working_types (id)
);

CREATE TABLE job_posting_confirmation_types(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	type CHARACTER VARYING(50) NOT NULL,
	CONSTRAINT pk_job_posting_confirmation_types PRIMARY KEY (id)
);

CREATE TABLE job_posting_confirmations(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	job_posting_id INTEGER NOT NULL,
	company_staff_id INTEGER NOT NULL,
	job_posting_confirmation_type_id INTEGER NOT NULL,
	is_confirmed BOOLEAN NOT NULL,
	is_confirmed_date TIMESTAMP WITH TIME ZONE NOT NULL,
	CONSTRAINT pk_job_posting_confirmations PRIMARY KEY (id),
	CONSTRAINT fk_job_posting_confirmations_job_postings FOREIGN KEY (job_posting_id) REFERENCES job_postings (id),
	CONSTRAINT fk_job_posting_confirmations_company_staffs FOREIGN KEY (company_staff_id) REFERENCES company_staffs (user_id),
	CONSTRAINT fk_job_posting_confirmations_job_posting_confirmation_types FOREIGN KEY (job_posting_confirmation_type_id) REFERENCES job_posting_confirmation_types (id)
);

CREATE TABLE favorite_job_postings(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	job_posting_id INTEGER NOT NULL,
	candidate_id INTEGER NOT NULL,
	date_of_add_to_favorites TIMESTAMP WITH TIME ZONE NOT NULL,
	CONSTRAINT pk_favorite_job_postings PRIMARY KEY (id),
	CONSTRAINT fk_favorite_job_postings_job_postings FOREIGN KEY (job_posting_id) REFERENCES job_postings (id),
	CONSTRAINT fk_favorite_job_postings_candidates FOREIGN KEY (candidate_id) REFERENCES candidates (user_id),
	CONSTRAINT uk_favorite_job_postings_job_posting_id_candidate_id UNIQUE (job_posting_id, candidate_id)
);

CREATE TABLE images(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	user_id INTEGER NOT NULL,
	url CHARACTER VARYING(150) NOT NULL,
	CONSTRAINT pk_pictures PRIMARY KEY (id),
	CONSTRAINT fk_pictures_users FOREIGN KEY (user_id) REFERENCES users (id)
);

CREATE TABLE cover_letters(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	candidate_id INTEGER NOT NULL,
	name CHARACTER VARYING(50) NOT NULL,
	content CHARACTER VARYING(1150) NOT NULL,
	CONSTRAINT pk_cover_letters PRIMARY KEY (id),
	CONSTRAINT fk_cover_letters_candidates FOREIGN KEY (candidate_id) REFERENCES candidates (user_id)
);

CREATE TABLE resumes(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	candidate_id INTEGER NOT NULL,
	cover_letter_id INTEGER,
	creation_date TIMESTAMP WITH TIME ZONE NOT NULL,
	CONSTRAINT pk_resumes PRIMARY KEY (id),
	CONSTRAINT fk_resumes_candidates FOREIGN KEY (candidate_id) REFERENCES candidates (user_id),
	CONSTRAINT fk_resumes_cover_letters FOREIGN KEY (cover_letter_id) REFERENCES cover_letters (id)
);

CREATE TABLE educations(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	resume_id INTEGER NOT NULL,
	name_of_educational_institution CHARACTER VARYING(150) NOT NULL,
	department CHARACTER VARYING(150) NOT NULL,
	degree CHARACTER VARYING(50) NOT NULL,
	starting_date DATE NOT NULL,
	graduation_date DATE,
	CONSTRAINT pk_educations PRIMARY KEY (id),
	CONSTRAINT fk_educations_resumes FOREIGN KEY (resume_id) REFERENCES resumes (id)
);

CREATE TABLE experiences(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	resume_id INTEGER NOT NULL,
	job_title_id INTEGER NOT NULL,
	company_name CHARACTER VARYING(150) NOT NULL,
	starting_date DATE NOT NULL,
	termination_date DATE,
	CONSTRAINT pk_experiences PRIMARY KEY (id),
	CONSTRAINT fk_experiences_resumes FOREIGN KEY (resume_id) REFERENCES resumes (id),
	CONSTRAINT fk_experiences_job_titles FOREIGN KEY (job_title_id) REFERENCES job_titles (id)
);

CREATE TABLE languages(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	language CHARACTER VARYING(50) NOT NULL,
	CONSTRAINT pk_languages PRIMARY KEY (id),
	CONSTRAINT uk_languages_language UNIQUE (language)
);

CREATE TABLE levels(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	level CHARACTER VARYING(50) NOT NULL,
	CONSTRAINT pk_levels PRIMARY KEY (id),
	CONSTRAINT uk_levels_level UNIQUE (level)
);

CREATE TABLE language_levels(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	resume_id INTEGER NOT NULL,
	language_id INTEGER NOT NULL,
	level_id INTEGER NOT NULL,
	CONSTRAINT pk_language_levels PRIMARY KEY (id),
	CONSTRAINT fk_language_levels_resumes FOREIGN KEY (resume_id) REFERENCES resumes (id),
	CONSTRAINT fk_language_levels_languages FOREIGN KEY (language_id) REFERENCES languages (id),
	CONSTRAINT fk_language_levels_levels FOREIGN KEY (level_id) REFERENCES levels (id)
);

CREATE TABLE link_names(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	name CHARACTER VARYING(50) NOT NULL,
	CONSTRAINT pk_link_names PRIMARY KEY (id)
);

CREATE TABLE links(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	resume_id INTEGER NOT NULL,
	link_name_id INTEGER NOT NULL,
	url CHARACTER VARYING(150) NOT NULL,
	CONSTRAINT pk_links PRIMARY KEY (id),
	CONSTRAINT fk_links_resumes FOREIGN KEY (resume_id) REFERENCES resumes (id),
	CONSTRAINT fk_links_link_names FOREIGN KEY (link_name_id) REFERENCES link_names (id)
);

CREATE TABLE skills(
	id INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
	resume_id INTEGER NOT NULL,
	skill CHARACTER VARYING(150) NOT NULL,
	CONSTRAINT pk_skills PRIMARY KEY (id),
	CONSTRAINT fk_skills_resumes FOREIGN KEY (resume_id) REFERENCES resumes (id)
);













